mapper.select:
SELECT * FROM todo
WHERE date = #{date} AND user_id = #{userId}
ORDER BY important DESC, id DESC

SELECT * FROM todo
WHERE user_id = #{userId}
ORDER BY date DESC, important DESC, id DESC

mapper.update:
UPDATE todo
text = #{fields.text}, completed = #{fields.completed}, important = #{fields.important}, category = #{fields.category}, icon = #{fields.icon},
WHERE id = #{id}
